import { Component, Input, OnChanges, SimpleChanges } from '@angular/core';
import { CommonModule } from '@angular/common';

type Role = 'log' | 'com' | 'admin' | 'commercial';

@Component({
  selector: 'app-sidebar',
  standalone: true,
  imports: [CommonModule],
  template: `
    <!-- HAMBURGER pour mobile (visible < lg) -->
    <button
      class="lg:hidden fixed top-4 left-4 z-40 w-10 h-10 bg-white rounded-lg shadow flex items-center justify-center"
      (click)="toggleMobile()"
      aria-label="Ouvrir le menu"
    >
      <svg width="20" height="14" viewBox="0 0 20 14" fill="none" xmlns="http://www.w3.org/2000/svg">
        <rect y="0" width="20" height="2" rx="1" fill="#111827"/>
        <rect y="6" width="20" height="2" rx="1" fill="#111827"/>
        <rect y="12" width="20" height="2" rx="1" fill="#111827"/>
      </svg>
    </button>
    <!-- SIDEBAR Desktop (lg and up) -->
    <aside
      class="hidden lg:flex w-[230px] lg:w-[230px] flex-col bg-gradient-to-b from-orange-400 to-orange-500 text-white fixed left-4 top-4 bottom-4 z-20 rounded-xl shadow-md"
      role="navigation"
      aria-label="Sidebar navigation"
    >
      <!-- Logo -->
      <div class="p-4 border-b border-orange-400">
        <div class="bg-white rounded-lg p-2 inline-block">
          <img src="images/logo.png" alt="coop achat" class="w-[210px] h-[44px] object-contain">
        </div>
      </div>
      <!-- Menu -->
      <nav class="flex-1 px-3 py-4 overflow-auto">
        <ul class="space-y-2">
          <li *ngFor="let item of filteredMenuItems" class="">
            <a
                [href]="item.link"
                class="flex items-center gap-3 p-3 rounded-lg transition-colors hover:bg-white/10"
                [ngClass]="{ 'bg-white/10 text-white': item.active }"
                [attr.aria-current]="item.active ? 'page' : null"
              >

              <img [src]="item.icon" alt="" class="w-5 h-5 object-contain" />
              <span class="text-sm font-medium">{{ item.label }}</span>
            </a>
          </li>
        </ul>
      </nav>

      <!-- Profile -->
      <div class="p-4 border-orange-400 relative">
        <div class="flex items-center gap-3">
          <svg width="38" height="39" viewBox="0 0 38 39" fill="none" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink">
                <circle cx="19" cy="19.5" r="18" fill="#D8D8D8" stroke="white" stroke-width="2"/>
                <mask id="mask0_83_3495" style="mask-type:luminance" maskUnits="userSpaceOnUse" x="1" y="1" width="36" height="37">
                <circle cx="19" cy="19.5" r="18" fill="white"/>
                </mask>
                <g mask="url(#mask0_83_3495)">
                <rect x="-16.5261" y="-3.71021" width="71.0526" height="45.9474" fill="url(#pattern0_83_3495)"/>
                </g>
                <defs>
                <pattern id="pattern0_83_3495" patternContentUnits="objectBoundingBox" width="1" height="1">
                <use xlink:href="#image0_83_3495" transform="scale(0.00116279 0.00179533)"/>
                </pattern>
                <image id="image0_83_3495" width="860" height="557" preserveAspectRatio="none" xlink:href="data:image/png;base64,"/>
                </defs>
          </svg>


            <div class="flex-1 min-w-0">
              <div class="text-sm font-medium truncate">Mohamed Ndiaye</div>
              <div class="text-xs text-orange-200 truncate">{{ roleLabel }}</div>
            </div>
          <button 
            class="ml-2 p-1 relative"
            (click)="toggleUserMenu()"
            aria-label="Menu utilisateur"
          >
            <svg width="19" height="18" viewBox="0 0 19 18" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M2.61575 18C2.06575 18 1.59509 17.8043 1.20375 17.413C0.81242 17.0217 0.61642 16.5507 0.615753 16V2C0.615753 1.45 0.811753 0.979333 1.20375 0.588C1.59575 0.196667 2.06642 0.000666667 2.61575 0H9.61575V2H2.61575V16H9.61575V18H2.61575ZM13.6158 14L12.2408 12.55L14.7908 10H6.61575V8H14.7908L12.2408 5.45L13.6158 4L18.6158 9L13.6158 14Z" fill="white"/>
            </svg>
          </button>
        </div>

        <!-- Menu déroulant utilisateur Desktop -->
        <div 
          *ngIf="userMenuOpen"
          class="absolute bottom-full right-0 mb-2 w-48 bg-white rounded-lg shadow-lg border border-gray-200 py-2 z-50"
        >
          <button
            class="w-full flex items-center gap-3 px-4 py-2 text-gray-700 hover:bg-gray-50 transition-colors text-left"
            (click)="goToProfile()"
          >
            <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                  <path d="M12 4C13.0609 4 14.0783 4.42143 14.8284 5.17157C15.5786 5.92172 16 6.93913 16 8C16 9.06087 15.5786 10.0783 14.8284 10.8284C14.0783 11.5786 13.0609 12 12 12C10.9391 12 9.92172 11.5786 9.17157 10.8284C8.42143 10.0783 8 9.06087 8 8C8 6.93913 8.42143 5.92172 9.17157 5.17157C9.92172 4.42143 10.9391 4 12 4ZM12 14C16.42 14 20 15.79 20 18V20H4V18C4 15.79 7.58 14 12 14Z" fill="#9CA3AF"/>
            </svg>
            <span class="text-sm">Mon compte</span>
          </button>
          
          <button
            class="w-full flex items-center gap-3 px-4 py-2 text-gray-700 hover:bg-gray-50 transition-colors text-left"
            (click)="logout()"
          >
            <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                  <path d="M5 21C4.45 21 3.97933 20.8043 3.588 20.413C3.19667 20.0217 3.00067 19.5507 3 19V5C3 4.45 3.196 3.97933 3.588 3.588C3.98 3.19667 4.45067 3.00067 5 3H12V5H5V19H12V21H5ZM16 17L14.625 15.55L17.175 13H9V11H17.175L14.625 8.45L16 7L21 12L16 17Z" fill="#9CA3AF"/>
            </svg>

            <span class="text-sm">Se déconnecter</span>
          </button>
        </div>
      </div>
    </aside>

    <!-- DRAWER Mobile (overlay) -->
    <div *ngIf="mobileOpen" class="lg:hidden fixed inset-0 z-40">
      <!-- Fond sombre -->
      <div class="absolute inset-0 bg-black/40" (click)="closeMobile()" aria-hidden="true"></div>

      <!-- Panneau qui glisse -->
      <aside class="relative w-72 max-w-full h-full bg-gradient-to-b from-orange-400 to-orange-500 text-white p-4 overflow-auto">
        <!-- Close -->
        <div class="flex items-center justify-between mb-4">
          <div class="bg-white rounded-lg p-2 inline-block">
            <img src="images/logo.png" alt="coop achat" class="w-44 h-10 object-contain">
          </div>
          <button (click)="closeMobile()" class="p-2 rounded-md bg-white/20 hover:bg-white/30" aria-label="Fermer le menu">
            <svg width="14" height="14" viewBox="0 0 14 14" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M1 1L13 13M13 1L1 13" stroke="white" stroke-width="2" stroke-linecap="round"/>
            </svg>
          </button>
        </div>

        <!-- Menu items mobile -->
        <nav class="mt-2">
          <ul class="space-y-2">
            <li *ngFor="let item of filteredMenuItems">
              <a
                [href]="item.link"
                class="flex items-center gap-3 p-3 rounded-lg transition-colors hover:bg-white/10"
                (click)="closeMobile()"
              >
                <img [src]="item.icon" alt="" class="w-5 h-5 object-contain" />
                <span class="text-sm font-medium">{{ item.label }}</span>
              </a>
            </li>
          </ul>
        </nav>

        <!-- profile mobile -->
        <div class="mt-6 pt-4 border-t border-orange-400 relative">
          <div class="flex items-center gap-3">
            <div class="w-10 h-10 bg-white rounded-full flex items-center justify-center overflow-hidden">
              <svg width="39" height="39" viewBox="0 0 39 39" fill="none" xmlns="http://www.w3.org/2000/svg">
                <circle cx="19.6157" cy="19.5" r="18" fill="#D8D8D8" stroke="white" stroke-width="2"/>
              </svg>
            </div>
            <div class="flex-1">
              <div class="text-sm font-medium">Mohamed Ndiaye</div>
              <div class="text-xs text-orange-200">{{ roleLabel }}</div>
            </div>
            <button 
              class="p-1"
              (click)="toggleUserMenuMobile()"
              aria-label="Menu utilisateur"
            >
              <svg width="19" height="18" viewBox="0 0 19 18" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M2.61575 18C2.06575 18 1.59509 17.8043 1.20375 17.413C0.81242 17.0217 0.61642 16.5507 0.615753 16V2C0.615753 1.45 0.811753 0.979333 1.20375 0.588C1.59575 0.196667 2.06642 0.000666667 2.61575 0H9.61575V2H2.61575V16H9.61575V18H2.61575ZM13.6158 14L12.2408 12.55L14.7908 10H6.61575V8H14.7908L12.2408 5.45L13.6158 4L18.6158 9L13.6158 14Z" fill="white"/>
              </svg>
            </button>
          </div>

          <!-- Menu déroulant utilisateur Mobile -->
          <div 
            *ngIf="userMenuMobileOpen"
            class="mt-3 bg-white/10 rounded-lg p-2 space-y-1"
          >
            <button
              class="w-full flex items-center gap-3 px-3 py-2 text-white hover:bg-white/10 rounded-md transition-colors text-left"
              (click)="goToProfile()"
            >
              <svg width="16" height="16" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <circle cx="12" cy="8" r="4" stroke="currentColor" stroke-width="2"/>
                <path d="M6 21v-2a4 4 0 0 1 4-4h4a4 4 0 0 1 4 4v2" stroke="currentColor" stroke-width="2"/>
              </svg>
              <span class="text-sm">Mon compte</span>
            </button>
            
            <button
              class="w-full flex items-center gap-3 px-3 py-2 text-white hover:bg-white/10 rounded-md transition-colors text-left"
              (click)="logout()"
            >
              <svg width="16" height="16" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M9 21H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h4" stroke="currentColor" stroke-width="2"/>
                <polyline points="16,17 21,12 16,7" stroke="currentColor" stroke-width="2"/>
                <line x1="21" y1="12" x2="9" y2="12" stroke="currentColor" stroke-width="2"/>
              </svg>
              <span class="text-sm">Se déconnecter</span>
            </button>
          </div>
        </div>
      </aside>
    </div>

    <!-- Overlay pour fermer les menus en cliquant à l'extérieur -->
    <div 
      *ngIf="userMenuOpen || userMenuMobileOpen"
      class="fixed inset-0 z-10"
      (click)="closeUserMenus()"
    ></div>
  `,
})
export class SidebarComponent implements OnChanges {
  @Input() role: Role = 'log';

  mobileOpen = false;
  userMenuOpen = false;
  userMenuMobileOpen = false;

  private readonly menuItems = [
    { label: 'Tableau de bord', icon: 'icones/dashboard.svg', link: '/com/dashboard', active: false },
    { label: 'Prospections', icon: 'icones/prospection.svg', link: '/com/propection', active: false },
    { label: 'Gestion salariés', icon: 'icones/salaries.svg', link: '/com/salaries', active: false },
    { label: 'Statistiques', icon: 'icones/statistiques.svg', link: '/com/statistiques', active: false },
    { label: 'Promotions', icon: 'icones/promotion.svg', link: '/com/promotions', active: false },
    
    { label: 'Tableau de bord', icon: 'icones/dashboard.svg', link: '/log/dashboardlog', active: false },
    { label: 'Commandes Fournisseurs', icon: 'icones/fourn.svg', link: '/log/fournisseurs', active: false },
    { label: 'Gestion des Stocks', icon: 'icones/stocks.svg', link: '/log/stocks', active: false },
    { label: 'Planification Livraisons', icon: 'icones/livraison.svg', link: '/log/livraison', active: false },
    { label: 'Gestion des Retours', icon: 'icones/retours.svg', link: '/log/retours', active: false },

    
    { label: 'Tableau de bord', icon: 'icones/dashboard.svg', link: '/admin/dashboardadmin', active: false },
    { label: 'Utilisateurs', icon: 'icones/users.svg', link: '/admin/users', active: false },
    { label: 'Catalogue', icon: 'icones/catalogue.svg', link: '/admin/catalogue', active: false },
    { label: 'Commandes', icon: 'icones/commandes.svg', link: '/admin/commandes', active: false },
    { label: 'Rapports', icon: 'icones/rapports.svg', link: '/admin/rapports', active: false },
  ];

  filteredMenuItems = this.menuItems.slice();
  roleLabel = 'Logistique';

  ngOnChanges(changes: SimpleChanges): void {
    if (changes['role']) {
      this.applyRoleFilter();
    }
  }

  private normalizeRole(input: Role): 'log' | 'com' | 'admin' {
    if (!input) return 'log';
    if (input === 'commercial') return 'com';
    if (input === 'com' || input === 'log' || input === 'admin') return input;
    return 'log';
  }

  private applyRoleFilter(): void {
    const normalized = this.normalizeRole(this.role);
    switch (normalized) {
      case 'log': this.roleLabel = 'Logistique'; break;
      case 'com': this.roleLabel = 'Commercial'; break;
      case 'admin': this.roleLabel = 'Administrateur'; break;
      default: this.roleLabel = 'Logistique';
    }

    const prefix = normalized === 'com' ? '/com' : normalized === 'log' ? '/log' : '/admin';
    this.filteredMenuItems = this.menuItems.filter(item => item.link.startsWith(prefix));
  }

  // Méthodes pour mobile
  toggleMobile() {
    this.mobileOpen = !this.mobileOpen;
  }

  closeMobile() {
    this.mobileOpen = false;
  }

  // Méthodes pour menu utilisateur desktop
  toggleUserMenu() {
    this.userMenuOpen = !this.userMenuOpen;
    this.userMenuMobileOpen = false;
  }

  // Méthodes pour menu utilisateur mobile
  toggleUserMenuMobile() {
    this.userMenuMobileOpen = !this.userMenuMobileOpen;
    this.userMenuOpen = false;
  }

  // Fermer tous les menus utilisateur
  closeUserMenus() {
    this.userMenuOpen = false;
    this.userMenuMobileOpen = false;
  }

  // Actions du menu utilisateur
  goToProfile() {
    console.log('Redirection vers Mon compte');
    // Implémentez ici la navigation vers la page de profil
    // Par exemple : this.router.navigate(['/profile']);
    this.closeUserMenus();
  }

  logout() {
    console.log('Déconnexion de l\'utilisateur');
    // Implémentez ici la logique de déconnexion
    // Par exemple : this.authService.logout(); this.router.navigate(['/login']);
    this.closeUserMenus();
  }
}